import ECharts from 'vue-echarts/components/ECharts';
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/legend';
import 'echarts/lib/chart/pie';
export default {
  data: function data() {
    return {
      pie: {
        tooltip: {
          trigger: 'item',
          formatter: '{a} <br/>{b} : {c} ({d}%)'
        },
        legend: {
          orient: 'vertical',
          left: 'left',
          data: ['Direct interview', 'Email marketing', 'Alliance advertising', 'Video ad', 'Search engine']
        },
        series: [{
          name: 'Access source',
          type: 'pie',
          radius: '55%',
          center: ['50%', '60%'],
          color: ['#FF9F43', '#28C76F', '#EA5455', '#87ceeb', '#7367F0'],
          data: [{
            value: 335,
            name: 'Direct interview'
          }, {
            value: 310,
            name: 'Email marketing'
          }, {
            value: 234,
            name: 'Alliance advertising'
          }, {
            value: 135,
            name: 'Video ad'
          }, {
            value: 1548,
            name: 'Search engine'
          }],
          itemStyle: {
            emphasis: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        }]
      }
    };
  },
  components: {
    ECharts: ECharts
  },
  mounted: function mounted() {
    var dataIndex = -1;
    var pie = this.$refs.pie;
    var dataLen = pie.options.series[0].data.length;
    setInterval(function () {
      pie.dispatchAction({
        type: 'downplay',
        seriesIndex: 0,
        dataIndex: dataIndex
      });
      dataIndex = (dataIndex + 1) % dataLen;
      pie.dispatchAction({
        type: 'highlight',
        seriesIndex: 0,
        dataIndex: dataIndex
      });
      pie.dispatchAction({
        type: 'showTip',
        seriesIndex: 0,
        dataIndex: dataIndex
      });
    }, 1000);
  }
};
