export default {
  TOGGLE_ITEM_IN_WISH_LIST: function TOGGLE_ITEM_IN_WISH_LIST(state, item) {
    var index = state.wishList.findIndex(function (i) {
      return i.objectID == item.objectID;
    });

    if (index < 0) {
      state.wishList.push(item);
    } else {
      state.wishList.splice(index, 1);
    }
  },
  REMOVE_ITEM_FROM_CART: function REMOVE_ITEM_FROM_CART(state, item) {
    var index = state.cartItems.findIndex(function (i) {
      return i.objectID == item.objectID;
    });

    if (index > -1) {
      state.cartItems.splice(index, 1);
    }
  },
  ADD_ITEM_IN_CART: function ADD_ITEM_IN_CART(state, item) {
    state.cartItems.push(Object.assign({}, item));
  },
  UPDATE_ITEM_QUANTITY: function UPDATE_ITEM_QUANTITY(state, payload) {
    state.cartItems[payload.index].quantity = payload.quantity;
  }
};
