import UserEditTabAccount from "./UserEditTabAccount.vue";
import UserEditTabInformation from "./UserEditTabInformation.vue";
import UserEditTabSocial from "./UserEditTabSocial.vue"; // Store Module

import moduleUserManagement from '@/store/user-management/moduleUserManagement.js';
export default {
  components: {
    UserEditTabAccount: UserEditTabAccount,
    UserEditTabInformation: UserEditTabInformation,
    UserEditTabSocial: UserEditTabSocial
  },
  data: function data() {
    return {
      user_data: null,
      user_not_found: false,

      /*
        This property is created for fetching latest data from API when tab is changed
         Please check it's watcher
      */
      activeTab: 0
    };
  },
  watch: {
    activeTab: function activeTab() {
      this.fetch_user_data(this.$route.params.userId);
    }
  },
  methods: {
    fetch_user_data: function fetch_user_data(userId) {
      var _this = this;

      this.$store.dispatch("userManagement/fetchUser", userId).then(function (res) {
        _this.user_data = res.data;
      }).catch(function (err) {
        if (err.response.status === 404) {
          _this.user_not_found = true;
          return;
        }

        console.error(err);
      });
    }
  },
  created: function created() {
    // Register Module UserManagement Module
    if (!moduleUserManagement.isRegistered) {
      this.$store.registerModule('userManagement', moduleUserManagement);
      moduleUserManagement.isRegistered = true;
    }

    this.fetch_user_data(this.$route.params.userId);
  }
};
