    exportToExcel: function exportToExcel() {
      var _this = this;

      // kiểm tra nếu chưa chọn đối tượng thình sẽ không hiện bản xuất file
      if (this.selectedUser.length == 0) {
        return this.$vs.notify({
          title: 'Xuất dữ liệu',
          text: 'Vui lòng chọn nội dung để hoàn thành',
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-heart'
        });
      }

      import('@/vendor/Export2Excel').then(function (excel) {
        var list = _this.selectedUser;

        var data = _this.formatJson(_this.headerVal, list);

        excel.export_json_to_excel({
          header: _this.headerTitle,
          data: data,
          filename: _this.fileName,
          autoWidth: _this.cellAutoWidth,
          bookType: _this.selectedFormat
        });

        _this.clearFields();
      });
    },
    formatJson: function formatJson(filterVal, jsonData) {
      return jsonData.map(function (v) {
        return filterVal.map(function (j) {
          // Add col name which needs to be translated
          // if (j === 'timestamp') {
          //   return parseTime(v[j])
          // } else {
          //   return v[j]
          // }
          return v[j];
        });
      });
    },
    clearFields: function clearFields() {
      this.fileName = "";
      this.cellAutoWidth = true;
      this.selectedFormat = "xlsx";
    } //==================================================================
