import 'quill/dist/quill.core.css';
import 'quill/dist/quill.snow.css';
import 'quill/dist/quill.bubble.css';
import { quillEditor } from 'vue-quill-editor';
export default {
  data: function data() {
    return {
      publicNews: true,
      title: '',
      content: "..."
    };
  },
  components: {
    quillEditor: quillEditor
  },
  computed: {
    slug: function slug() {
      var slug = this.sanitizeTitle(this.title);
      return location.host + '/' + slug;
    }
  },
  methods: {
    successUpload: function successUpload() {//this.$vs.notify({color:'success',title:'Upload Success',text:'Lorem ipsum dolor sit amet, consectetur'})
    },
    submitForm: function submitForm() {
      var _this = this;

      this.$validator.validateAll().then(function (result) {
        if (result) {
          // if form have no errors
          _this.$vs.notify({
            color: 'success',
            title: 'Đăng bài',
            text: 'Thành công'
          });
        } else {// form have errors
        }
      });
    },
    plublicPostNews: function plublicPostNews() {
      this.publicNews ? this.publicNews = false : this.publicNews = true;
    },
    sanitizeTitle: function sanitizeTitle(title) {
      var slug = ""; // Change to lower case

      var titleLower = title.toLowerCase(); // Letter "e"

      slug = titleLower.replace(/e|é|è|ẽ|ẻ|ẹ|ê|ế|ề|ễ|ể|ệ/gi, 'e'); // Letter "a"

      slug = slug.replace(/a|á|à|ã|ả|ạ|ă|ắ|ằ|ẵ|ẳ|ặ|â|ấ|ầ|ẫ|ẩ|ậ/gi, 'a'); // Letter "o"

      slug = slug.replace(/o|ó|ò|õ|ỏ|ọ|ô|ố|ồ|ỗ|ổ|ộ|ơ|ớ|ờ|ỡ|ở|ợ/gi, 'o'); // Letter "u"

      slug = slug.replace(/u|ú|ù|ũ|ủ|ụ|ư|ứ|ừ|ữ|ử|ự/gi, 'u'); // Letter "d"

      slug = slug.replace(/đ/gi, 'd'); // Letter ","

      slug = slug.replace(/,/gi, ''); // Trim the last whitespace

      slug = slug.replace(/\s*$/g, ''); // Change whitespace to "-"

      slug = slug.replace(/\s+/g, '-');
      return slug;
    }
  }
};
