import VuePerfectScrollbar from 'vue-perfect-scrollbar';
export default {
  props: {
    userId: {
      type: Number,
      required: true
    },
    active: {
      type: Boolean,
      required: true
    },
    isLoggedInUser: {
      type: Boolean,
      required: true
    }
  },
  data: function data() {
    return {
      counterDanger: false,
      settings: {
        // perfectscrollbar settings
        maxScrollbarLength: 60,
        wheelSpeed: .60
      }
    };
  },
  computed: {
    chatUser: function chatUser() {
      return this.$store.getters['chat/chatUser'](this.userId);
    },
    activeLocal: {
      get: function get() {
        return this.active;
      },
      set: function set(value) {
        this.$emit('closeProfileSidebar', value);
      }
    },
    about: {
      get: function get() {
        return this.chatUser.about;
      },
      set: function set(value) {
        this.$store.dispatch('updateUserInfo', {
          about: value
        });
      }
    },
    status: {
      get: function get() {
        return this.chatUser.status;
      },
      set: function set(value) {
        this.$store.dispatch('updateUserInfo', {
          status: value
        });
      }
    },
    statusColor: function statusColor() {
      var userStatus = this.chatUser.status;

      if (userStatus == "online") {
        return "success";
      } else if (userStatus == "do not disturb") {
        return "danger";
      } else if (userStatus == "away") {
        return "warning";
      } else {
        return "grey";
      }
    }
  },
  components: {
    VuePerfectScrollbar: VuePerfectScrollbar
  }
};
