import AuthenticationService from '@/services/AuthenticationService';
import SETTINGS from "../../../settings.json";
export default {
  // components: {
  //   DataViewSidebar
  // },
  data: function data() {
    return {
      dm: SETTINGS.BASE_URL,
      imgLinkDef: require("@/assets/images/profile/sfp.png"),
      imgLink: require("@/assets/images/profile/sfp.png"),
      popupViewImg: false,
      selected: [],
      productsFake: [{
        "id": 1,
        "email": "manhduc@gmail.com",
        "nick_name": "SkyPlaza",
        "first_name": "Ares",
        "id_front": null,
        "id_back": null,
        "verified": 0
      }, {
        "id": 2,
        "email": "manhduc23@gmail.com",
        "nick_name": "SkyPlaza 2",
        "first_name": "Tùng",
        "id_front": null,
        "id_back": null,
        "verified": 1
      }],
      itemsPerPage: 10,
      isMounted: false,
      // Data Sidebar
      addNewDataSidebar: false,
      sidebarData: {}
    };
  },
  computed: {
    currentPage: function currentPage() {
      if (this.isMounted) {
        return this.$refs.table.currentx;
      }

      return 0;
    },
    products: function products() {
      return this.productsFake;
    },
    queriedItems: function queriedItems() {
      return this.$refs.table ? this.$refs.table.queriedResults.length : this.productsFake.length;
    }
  },
  methods: {
    doneVerify: function doneVerify(id, val, index) {
      var _this = this;

      var obj = {
        id: id,
        verified: val
      };

      if (val) {
        this.productsFake[index].verified = 1;
      } else {
        this.productsFake[index].verified = 0;
      }

      AuthenticationService.verifiedUser(obj).then(function (resq) {
        if (resq.data.success) {
          return _this.$vs.notify({
            text: val ? 'Xác minh thành công' : 'Hủy xác minh thành công',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-check'
          });
        } else {
          return _this.$vs.notify({
            text: 'Xác minh tài khoản thất bại',
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-alert-circle'
          });
        }
      });
    },
    mediaType: function mediaType(img) {
      if (img != null) {
        return this.dm + 'api/auth/me/photo/passport/' + img;
      } else {
        return this.imgLinkDef;
      }
    },
    getPopupViewImg: function getPopupViewImg(link) {
      if (link != null) {
        this.imgLink = this.dm + 'api/auth/me/photo/passport/' + link;
      } else {
        this.imgLink = this.imgLinkDef;
      }

      this.popupViewImg = true;
    },
    getOrderStatusColor: function getOrderStatusColor(status) {
      if (status == 0) return "warning";
      if (status == 1) return "success"; //if(status == 2) return "danger"

      return "warning";
    },
    getOrderStatusColorText: function getOrderStatusColorText(status) {
      if (status == 0) return "Chưa xác minh";
      if (status == 1) return "Đã xác minh"; //if(status == 2) return "danger"

      return "Chưa xác minh";
    },
    openLoadingInDiv: function openLoadingInDiv() {
      this.$vs.loading({
        container: '#loading-corners',
        type: 'corners',
        scale: 0.6
      });
    }
  },
  created: function created() {
    var _this2 = this;

    // if(!moduleDataList.isRegistered) {
    //   this.$store.registerModule('dataList', moduleDataList)
    //   moduleDataList.isRegistered = true
    // }
    //this.$store.registerModule('dataList', this.productsFake);
    //this.$store.dispatch("dataList/fetchDataListItems")
    //console.log(this.productsFake);
    //console.log(this.$store.state.dataList);
    //let token = localStorage.getItem('token')
    //this.$store.dispatch('setToken', token)
    AuthenticationService.getAllMember().then(function (resp) {
      _this2.$vs.loading.close('#loading-corners > .con-vs-loading');

      if (resp.data.success == 4) {
        localStorage.removeItem('token');

        _this2.$router.push('/pages/login').catch(function () {});
      } else {
        _this2.productsFake = resp.data.data;
      }
    });
  },
  mounted: function mounted() {
    this.isMounted = true;
    this.openLoadingInDiv();
  }
};
