import moment from 'moment';
import Vue from 'vue';
export default {
  components: {
    vSelect: vSelect
  },
  data: function data() {
    return {
      showDeleteMultiBt: true,
      activePrompt: false,
      selectedUser: [],
      fileName: "",
      formats: ["xlsx", "csv", "txt"],
      cellAutoWidth: true,
      selectedFormat: "xlsx",
      headerTitle: ["Tài khoản", "Loại", "Số Tiền", "Trạng Thái", "Ngày Nạp"],
      headerVal: ["account", "type", "amount", "status", "datecreate"],
      productsFake: [{
        "id": 1,
        "from_u": "123",
        "to_u": "5344",
        "type_key": "nt",
        "type": "Nạp tiền (Nội bộ)",
        "currency": "sys",
        "amount": 100.00,
        "note": "",
        "status": 1,
        "created_at": "00:00:00 02-04-2021"
      }, {
        "id": 2,
        "from_u": "123",
        "to_u": "5344",
        "type_key": "nt",
        "type": "Nạp tiền (Nội bộ)",
        "currency": "BTC",
        "amount": 100.00,
        "note": "ok nha",
        "status": 1,
        "created_at": "00:00:00 02-04-2021"
      }, {
        "id": 3,
        "from_u": "123",
        "to_u": "5344",
        "type_key": "nt",
        "type": "Nạp tiền (Nội bộ)",
        "currency": "ETH",
        "amount": 100.00,
