        "from_u": "123",
        "to_u": "5344",
        "type_key": "nt",
        "type": "Nạp tiền (Nội bộ)",
        "currency": "USDT",
        "amount": 100.00,
        "note": "",
        "status": 1,
        "created_at": "00:00:00 02-04-2021"
      }],
      itemsPerPage: 10,
      isMounted: false,
      dtUSD: 0,
      dtBNB: 0,
      feeBNB: 0
    };
  },
  computed: {
    currentPage: function currentPage() {
      if (this.isMounted) {
        return this.$refs.table.currentx;
      }

      return 0;
    },
    products: function products() {
      return this.productsFake;
    },
    queriedItems: function queriedItems() {
      return this.$refs.table ? this.$refs.table.queriedResults.length : this.productsFake.length;
    }
  },
  methods: {
    deleteMultiple: function deleteMultiple() {
      var _this = this;

      var token = localStorage.getItem('token');
      this.$store.dispatch('setToken', token);

      if (this.selectedUser.length == 0) {
        return this.$vs.notify({
          text: 'Hãy chọn đối tượng cần xóa',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }

      for (var i = this.selectedUser.length - 1; i >= 0; i--) {
        var id = this.selectedUser[i]['id'];
        var obj = {
          id: id,
          val: 1
        };
        AuthenticationService.deleteTrashByID(obj).then(function (resp) {
          if (!resp.data.success) {
            localStorage.removeItem('token');

            _this.$router.push('/pages/login').catch(function () {});
          }
        });
        Vue.delete(this.productsFake, i);
      }

      this.selectedUser = [];
      return this.$vs.notify({
        text: 'Đã xóa thành công',
        color: 'success',
        iconPack: 'feather',
        icon: 'icon-check'
      });
    },
    deleteDeposit: function deleteDeposit(id, index, val) {
      var _this2 = this;

      var token = localStorage.getItem('token');
      this.$store.dispatch('setToken', token);
      var obj = {
        id: id,
        val: val
      };
      AuthenticationService.deleteTrashByID(obj).then(function (resp) {
        if (resp.data.success) {
          Vue.delete(_this2.productsFake, index);
          _this2.popupDeleteActive = false;
          return _this2.$vs.notify({
            text: 'Đã xóa thành công',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-check'
          });
        } else {
          localStorage.removeItem('token');

          _this2.$router.push('/pages/login').catch(function () {});
        }
      });
    },
    trashDataDeposit: function trashDataDeposit() {
      var _this3 = this;

      this.showDeleteMultiBt = false;
      var token = localStorage.getItem('token');
      this.$store.dispatch('setToken', token);
      AuthenticationService.getDepositAllTrash().then(function (resp) {
        if (!resp.data.success) {
          localStorage.removeItem('token');

          _this3.$router.push('/pages/login').catch(function () {});
        } else {
          _this3.productsFake = resp.data.data;
        }
      });
    },
    getOrderStatusColor: function getOrderStatusColor(status) {
      if (status == 0) return "warning";
      if (status == 1) return "success"; //if(status == 2) return "danger"

      return "warning";
    },
    getOrderStatusColorText: function getOrderStatusColorText(status) {
      if (status == 0) return "Đang xử lý";
      if (status == 1) return "Hoàn thành"; //if(status == 2) return "danger"

      return "Đang xử lý";
    },
    getIconType: function getIconType(type) {
      var cur = type.toUpperCase();
      return cur;
    },
    formatDate: function formatDate(value) {
      if (value) {
        return moment(String(value)).format('MM/DD/YYYY hh:mm:ss');
      }
    },
    formatPrice: function formatPrice(value, minimum) {
      if (void 0 === value) value = 0;
      var formatter = new Intl.NumberFormat('en-US', {
        //style: 'currency',
        //currency: '',
        minimumFractionDigits: minimum
      });
      return formatter.format(value);
    },
    getAmountDecimal: function getAmountDecimal(type, value) {
      var cur = '$';
      var coin = type.toUpperCase();
      var minimum = 2;
      if (coin == 'BTC') minimum = 6;
      if (coin == 'ETH') minimum = 4;
      if (coin == 'USDT') minimum = 2;
      if (coin == 'VN') minimum = 0;
      var formatter = new Intl.NumberFormat('en-US', {
        //style: 'currency',
        //currency: '',
        minimumFractionDigits: minimum
      });
      return cur + formatter.format(value);
    },
    toggleDataSidebar: function toggleDataSidebar() {
      var val = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : false;
      this.addNewDataSidebar = val;
    },
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    // export ra file
    //==================================================================
    exportToExcel: function exportToExcel() {
      var _this4 = this;

      // kiểm tra nếu chưa chọn đối tượng thình sẽ không hiện bản xuất file
      if (this.selectedUser.length == 0) {
        return this.$vs.notify({
          title: 'Xuất dữ liệu',
          text: 'Vui lòng chọn nội dung để hoàn thành',
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-heart'
        });
      }

      import('@/vendor/Export2Excel').then(function (excel) {
        var list = _this4.selectedUser;

        var data = _this4.formatJson(_this4.headerVal, list);

        excel.export_json_to_excel({
          header: _this4.headerTitle,
          data: data,
          filename: _this4.fileName,
          autoWidth: _this4.cellAutoWidth,
          bookType: _this4.selectedFormat
        });

        _this4.clearFields();
      });
    },
    formatJson: function formatJson(filterVal, jsonData) {
      return jsonData.map(function (v) {
        return filterVal.map(function (j) {
          // Add col name which needs to be translated
          // if (j === 'timestamp') {
          //   return parseTime(v[j])
          // } else {
          //   return v[j]
          // }
          return v[j];
        });
      });
    },
    clearFields: function clearFields() {
      this.fileName = "";
      this.cellAutoWidth = true;
      this.selectedFormat = "xlsx";
    },
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    openLoadingInDiv: function openLoadingInDiv() {
      this.$vs.loading({
        container: '#loading-corners',
        type: 'corners',
        scale: 0.6
      });
    },
    reloadList: function reloadList() {
      var _this5 = this;

      this.showDeleteMultiBt = true;
      var token = localStorage.getItem('token');
      this.$store.dispatch('setToken', token);
      AuthenticationService.getDepositListHistory().then(function (resp) {
        _this5.$vs.loading.close('#loading-corners > .con-vs-loading');

        if (resp.data.success == 4) {
          localStorage.removeItem('token');

          _this5.$router.push('/pages/login').catch(function () {});
        } else {
          _this5.productsFake = resp.data.data;
        }
      });
    },
    getDTNap: function getDTNap() {
      var _this6 = this;

      AuthenticationService.getRevenueNap().then(function (resp) {
        var g = resp.data;

        if (g.success == 1) {
          _this6.dtUSD = g.data.dtUSD;
          _this6.dtBNB = g.data.dtBNB;
          _this6.feeBNB = g.data.freeBNB;
        }
      });
    }
  },
  created: function created() {
    // if(!moduleDataList.isRegistered) {
    //   this.$store.registerModule('dataList', moduleDataList)
    //   moduleDataList.isRegistered = true
    // }
    //this.$store.registerModule('dataList', this.productsFake);
    //this.$store.dispatch("dataList/fetchDataListItems")
    //console.log(this.productsFake);
    //console.log(this.$store.state.dataList);
    this.reloadList();
    this.getDTNap();
  },
  mounted: function mounted() {
    this.isMounted = true;
    this.openLoadingInDiv();
  }
};
