@media screen and (max-width: 600px) {
    .wrapper-notification-1 {
        .ttM {
            white-space: nowrap;
        }
    }

    .dropdown-custom {
        width: 90vw !important;
    }
}

.dropdown-custom {
    box-shadow: none;
    border: none;
    width: 400px;
    max-height: 450px;
}

.bordered-items {
    border: 1px solid #383442;
    background: rgb(25, 33, 43);
}

.notification-dropdown-button-number {
    position: absolute;
    top: -10px;
    left: calc(50% + 2px);
    display: inline-block;
    overflow: hidden;
    background: #e22a67;
    color: #fff;
    min-width: 22px;
    height: 22px;
    line-height: 14px;
    border-radius: 50%;
    font-weight: bold;
    font-size: 9px;
    border: 2px solid #02142b;
    padding: 0 2px;
    display: flex;
    justify-content: center;
    align-items: center;
}

.no-notification {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 50px 15px;
    color: #878098;
    font-size: 14px;
    font-weight: bold;
}

.wrapper-notification {
    background: rgb(25, 33, 43);
    padding: 13px 20px;
    display: flex;
    justify-content: space-between;

    .left {
        font-size: 18px;
        font-weight: bold;
        color: #fff;
        line-height: 24px;
        margin-bottom: 0;

        display: flex;
        justify-content: center;
        align-items: center;

        svg {
            margin-left: 5px;
            cursor: pointer;
        }
    }

    .right {
        font-size: 14px;
        color: #878098;
        line-height: 24px;
        font-weight: normal;
        cursor: pointer;
    }
}
@media screen and (max-width: 600px) {

    .notification-dropdown-button-number{
        top: -10px;
        min-width: 22px;
        height: 22px;
        line-height: 18px;
        font-size: 12px;
    }

}
