import vSelect from 'vue-select'
import AuthenticationService from '@/services/AuthenticationService'
import moment from 'moment'
import Vue from 'vue'
//import LoadingTypeVue from '../../views/components/vuesax/loading/LoadingType.vue'

export default {
  components: {
    vSelect
  },



  data() {
    return {
      showDeleteMultiBt: true,
      activePrompt: false,
      selectedUser: [],
      fileName: "",
      formats:["xlsx", "csv", "txt"],
      cellAutoWidth: true,
      selectedFormat: "xlsx",
      headerTitle: ["ID", "Mô tả", "Số Tiền", "Ghi Chú", "Ngày Giao Dịch" ],
      headerVal: ["id", "account", "amount", "note", "datecreate" ],
      productsFake: [
            {
              "id": 1,
              "from_u": "123",
              "to_u": "5344",
              "type_key": "hh", 
              "type": "Hoa hồng Giao dịch",
              "currency": "sys",
              "amount": 100.00,
              "note": "",
              "status": 1,
              "created_at": "00:00:00 02-04-2021"
            },
            {
              "id": 2,
              "from_u": "123",
              "to_u": "0x9459287648927947298453",
              "type_key": "rt", 
              "type": "Rút tiền Onchain",
              "currency": "BTC",
              "amount": 100.00,
              "note": "",
              "status": 0,
              "created_at": "00:00:00 02-04-2021"
            },
            {
              "id": 3,
              "from_u": "123",
              "to_u": "0x9459287648927947298453",
              "type_key": "rt", 
              "type": "Rút tiền ERC20",
              "currency": "ETH",
              "amount": 100.00,
              "note": "",
              "status": 0,
              "created_at": "00:00:00 02-04-2021"
            },
            {
              "id": 4,
              "from_u": "123",
              "to_u": "skyNice",
              "type_key": "rt", 
              "type": "Rút tiền (Nội bộ)",
              "currency": "sys",
              "amount": 100.00,
              "note": "",
              "status": 1,
              "created_at": "00:00:00 02-04-2021"
            },
            {
              "id": 5,
              "from_u": "5436434",
              "to_u": "skyNice",
              "type_key": "ct", 
              "type": "Chuyển tiền từ Live Account -> (Nội bộ)",
              "currency": "sys",
              "amount": 100.00,
              "note": "",
              "status": 1,
              "created_at": "00:00:00 02-04-2021"
            },
            {
              "id": 6,
              "from_u": "skyNice",
              "to_u": "431344",
              "type_key": "rt", 
              "type": "Chuyển tiền từ (Nội bộ) -> Live Account",
              "currency": "sys",
              "amount": 100.00,
              "note": "",
              "status": 1,
              "created_at": "00:00:00 02-04-2021"
            },
        ],
      itemsPerPage: 10,
      isMounted: false,
      dtUSD: 0,
      dtBNB: 0,
      feeBNB: 0,
      dtUSDR: 0,
      dtBNBR: 0,
      feeBNBR: 0
    }
  },
  computed: {
    currentPage() {
      if(this.isMounted) {
        return this.$refs.table.currentx
      }
      return 0
    },
    products() {
      return this.productsFake
    },
    queriedItems() {
      return this.$refs.table ? this.$refs.table.queriedResults.length : this.productsFake.length
    }
  },
  methods: {
    deleteMultiple(){
      let token = localStorage.getItem('token')
      this.$store.dispatch('setToken', token)

      if(this.selectedUser.length == 0){
        return this.$vs.notify({
          text:'Hãy chọn đối tượng cần xóa',
          color:'warning',
          iconPack: 'feather',
          icon:'icon-check'});
      }
      for(var i = this.selectedUser.length - 1; i >= 0 ;i--){
          let id = this.selectedUser[i]['id'];
          let obj = {
            id: id,
            val: 1
          }
          AuthenticationService.deleteTrashByID(obj)
          .then((resp) => {
            if(!resp.data.success){
              localStorage.removeItem('token');
              this.$router.push('/pages/login').catch(() => {})
            }
          })
          Vue.delete(this.productsFake, i)
      }
      this.selectedUser = []
      return this.$vs.notify({
          text:'Đã xóa thành công',
          color:'success',
          iconPack: 'feather',
          icon:'icon-check'});
    },
    deleteTrade(id, index, val) {
      let token = localStorage.getItem('token')
      this.$store.dispatch('setToken', token)
      let obj = {
        id: id,
        val: val
      }
      AuthenticationService.deleteTrashByID(obj)
        .then((resp) => {
          if(resp.data.success){
              Vue.delete(this.productsFake, index)
              this.popupDeleteActive = false
              return this.$vs.notify({
                  text:'Đã xóa thành công',
                  color:'success',
                  iconPack: 'feather',
                  icon:'icon-check'});
          }else{
            localStorage.removeItem('token');
            this.$router.push('/pages/login').catch(() => {})
          }
        })
    },

    getOrderStatusColor(status) {
      if(status == 0) return "warning"
      if(status == 1) return "success"
      if(status == 3) return "danger"
      //if(status == 2) return "danger"
      return "warning"
    },
    getOrderStatusColorText(status) {
      if(status == 0) return "Chờ xử lý"
      if(status == 1) return "Hoàn thành"
      if(status == 3) return "Không giải quyết"
      //if(status == 2) return "danger"
      return "Chờ xử lý"
    },

    getIconType(type){
      let cur = type;
      if(cur == null) cur = 'usdt'
      return cur
    },

    formatDate(value){
      if (value) {
          return moment(String(value)).format('MM/DD/YYYY hh:mm:ss')
      }
    },

    formatPrice(value, minimum){
      if(void 0 === value) value = 0;
      var formatter = new Intl.NumberFormat('en-US', {
          //style: 'currency',
          //currency: '',
          minimumFractionDigits: minimum
      });
      
      return formatter.format(value);
    },

    getAmountDecimal(type, value){
      
      if(type == null){
        type = 'usdt'
      }

      let cur = '$'
      let coin = type.toUpperCase();
      let minimum = 2
      if(coin == 'BTC') minimum = 6
      
      if(coin == 'ETH') minimum = 4

      if(coin == 'USDT') minimum = 2

      //if(coin == 'VN') minimum = 0

     

      var formatter = new Intl.NumberFormat('en-US', {
          //style: 'currency',
          //currency: '',
          minimumFractionDigits: minimum
      });
      
      return cur+formatter.format(value);
    },

    toggleDataSidebar(val=false) {
      this.addNewDataSidebar = val
    },


    getTrashTrade(){
      this.showDeleteMultiBt = false
      let token = localStorage.getItem('token')
      this.$store.dispatch('setToken', token)

      AuthenticationService.gethistoryAllTrash()
      .then((resp) => {
        if(!resp.data.success){
            localStorage.removeItem('token');
            this.$router.push('/pages/login').catch(() => {})
        }else{
            this.productsFake = resp.data.data;
        }
      })
      
    },




    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    // export ra file
    //==================================================================
    exportToExcel() {
        // kiểm tra nếu chưa chọn đối tượng thình sẽ không hiện bản xuất file
      if(this.selectedUser.length == 0){
        return this.$vs.notify({
            title:'Xuất dữ liệu',
            text:'Vui lòng chọn nội dung để hoàn thành',
            color:'danger',
            iconPack: 'feather',
            icon:'icon-heart'});
      } 
      import('@/vendor/Export2Excel').then(excel => {
        const list = this.selectedUser
        const data = this.formatJson(this.headerVal, list)
        excel.export_json_to_excel({
          header: this.headerTitle,
          data,
          filename: this.fileName,
          autoWidth: this.cellAutoWidth,
          bookType: this.selectedFormat
        })
        this.clearFields()
      })
    },
    formatJson(filterVal, jsonData) {
      return jsonData.map(v => filterVal.map(j => {
        // Add col name which needs to be translated
        // if (j === 'timestamp') {
        //   return parseTime(v[j])
        // } else {
        //   return v[j]
        // }

        return v[j]
      }))
    },
    clearFields() {
      this.fileName = ""
      this.cellAutoWidth = true
      this.selectedFormat = "xlsx"
    },

    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
    //==================================================================
      openLoadingInDiv(){
        this.$vs.loading({
            container: '#loading-corners',
            type: 'corners',
            scale: 0.6
        })
    },

       reloadList(){
          this.showDeleteMultiBt = true
          let token = localStorage.getItem('token')
          this.$store.dispatch('setToken', token)

          AuthenticationService.getTradeListHistory()
          .then((resp) => {
            
            this.$vs.loading.close('#loading-corners > .con-vs-loading');

            if(!resp.data.success){
                localStorage.removeItem('token');
                this.$router.push('/pages/login').catch(() => {})
            }else{
                this.productsFake = resp.data.data;
            }
          })
    },

    getDTNap(){
            AuthenticationService.getRevenueNap()
            .then((resp) => {
                let g = resp.data;
                if(g.success == 1){
                  this.dtUSD = g.data.dtUSD;
                  this.dtBNB = g.data.dtBNB;
                  this.feeBNB = g.data.freeBNB;
                }
                
            })
      },

      getDTRut(){
            AuthenticationService.getRevenueRut()
            .then((resp) => {
                let g = resp.data;
                if(g.success == 1){
                  this.dtUSDR = g.data.dtUSD;
                  this.dtBNBR = g.data.dtBNB;
                  this.feeBNBR = g.data.freeBNB;
                }
                
            })
      },

  },
  created() {
    // if(!moduleDataList.isRegistered) {
    //   this.$store.registerModule('dataList', moduleDataList)
    //   moduleDataList.isRegistered = true
    // }

    //this.$store.registerModule('dataList', this.productsFake);
    
    //this.$store.dispatch("dataList/fetchDataListItems")
    //console.log(this.productsFake);
    //console.log(this.$store.state.dataList);
    this.reloadList();
    this.getDTNap();
    this.getDTRut();

  },
  mounted() {
    this.isMounted = true;
    this.openLoadingInDiv();
  }
}
