.e-c-base {
  fill: none;
  stroke: #B6B6B6;
  stroke-width: 12px
}
.red .e-c-progress {
  fill: none;
  stroke: #ff0000;
  stroke-width: 12px;
  transition: stroke-dashoffset 0.7s;
}
.green .e-c-progress {
  fill: none;
  stroke: #31baa0;
  stroke-width: 12px;
  transition: stroke-dashoffset 0.7s;
}
.red .e-c-progress {
  fill: none;
  stroke: #ff0000;
  stroke-width: 10px;
  transition: stroke-dashoffset 0.7s;
}

.red .e-c-pointer {
  fill: #FFF;
  stroke: #ff0000;
  stroke-width: 10px;
}

.green .e-c-progress {
  fill: none;
  stroke: #31baa0;
  stroke-width: 10px;
  transition: stroke-dashoffset 0.7s;
}

.green .e-c-pointer {
  fill: #FFF;
  stroke:#31baa0;
  stroke-width: 10px;
}
#e-pointer { transition: transform 0.7s; }
