export default {
    data() {
        return {
            activePrompt: false,

            taskLocal: {
              title: "",
              desc: "",
              isCompleted: false,
              isImportant: false,
              isStarred: false,
              tags: []
            }
        }
    },
    computed: {
        taskTags() {
            return this.$store.state.todo.taskTags
        },
        validateForm() {
            return !this.errors.any() && this.taskLocal.title != ''
        }
    },
    methods: {
        clearFields() {
            Object.assign(this.taskLocal, {
              title: "",
              desc: "",
              isCompleted: false,
              isImportant: false,
              isStarred: false,
              tags: []
            })
        },
        addTodo() {
            this.$validator.validateAll().then(result => {
                if (result) {
                  this.$store.dispatch('todo/addTask', Object.assign({}, this.taskLocal))
                  this.clearFields()
                }
            })
        }
    },
}
