.wrapper {
  padding: 30px;
}

.title {
  font-size: 38px;
  font-weight: 700;
  color: #fff;
  line-height: 1;
  padding-bottom: 18px;
  border-bottom: 1px solid #011022;
  margin-bottom: 0;
}

.wrapper-list-noti {
  .content {
    display: flex;
    margin-bottom: 20px;
    padding: 10px;
    transition: .3s;
    border-radius: 5px;

    &:hover {
      background: #1f2f43;
    }

    .body {
      position: relative;
      flex: 1;
    }
    .icon {
      display: flex;
      justify-content: center;
      align-items: center;
      svg {
        width: 20px;
        height: 20px;
      }
      width: 32px;
      height: 32px;
      border-radius: 32px;
      background: #1f2f43;

      margin-right: 10px;
    }

    .tit {
      font-size: 14px;
      font-weight: 700;
      line-height: 20px;
      color: #fff;
      margin-bottom: 5px;
    }

    .des {
      font-size: 12px;
      color: #8b8d96;
      line-height: 16px;
      margin-bottom: 8px;
    }

    .time {
      background: rgba(58,63,101,.4);
      border-radius: 3px;
      display: inline-block;
      height: 24px;
      padding: 0 8px;
      font-size: 12px;
      line-height: 24px;
      color: hsla(0,0%,100%,.44);
    }

    .dot {
      position: absolute;
      top: 0%;
      right: 0;
      width: 10px;
      height: 10px;
      border-radius: 10px;
      background: #e5b000;
    }
  }
}
