    components: {
        highcharts: Chart,
        VueApexCharts,
        Datepicker,
        'v-select': vSelect,
    },
    data() {
        return {
            hostName: window.location.hostname,
            showGobal: true,
            showCom: false,
            showqlHt: false,
            showTT: false,
            showTN: true,

            optionsSpline: chartOne,
