            selectedHoaHong: {id: 1, label: 'Hoa hồng Giao dịch'},
            popupActiveRank: false,
            popupActiveCommission: false,
            formatDate: "dd/MM/yyyy",
            startDate: null,
            endDate: null,

            hhMeF1isWeek: 0,
            totalDlisMe: 0,

            totalDaiLy: 0,
            vip_lv: 1,
            pen_commiss: 0,
            totalNGD: 0,
            commissGD: 0,

            klgdtc1: 0,
            klgdtc2: 0,
            klgdtc3: 0,
            klgdtc4: 0,

            t1: '',
            t2: '',
            t3: '',
            t4: '',
            
            tslgdmtn: 0,
            tshhmtn: 0,
            tslgdmtt: 0,
            tshhmtt: 0,
