            tsdlmtt: 0,

            listChiTietKLGD: [],
            listChiTietKLVip: [],
            countPageHH: 0,
            countPageHHVip: 0,

            dateListDLSearch: 0,

            oneday: false,
            twoday: false,
            threeday: true,
            allday: false,

            datalistAgency: [],

            showhhGD: true
        }
    },

