                          <p class="text-sm mt-3 color-grey text-center">Bạn chưa có lệnh đóng nào</p>
                      </div>
                      <div v-else class="flex flex-col justify-center mb-20">
                        <div :key="indextr" v-for="(oknha, indextr) in listItem">
                              <div class="transactionBox text-sm mb-2">
                                  <div class="flex justify-between mb-2">
                                      <span class="text-sm font-bold text-white">{{ oknha.c }}
                                          <span class="accType text-xs">{{ oknha.t ? 'LIVE' : 'DEMO' }}</span>
                                      </span> 
                                      <span class="icon iconBitcoin"></span>
                                  </div>
                                  <div class="flex justify-between mb-2 text-white">
                                      <span class="trendGroup flex align-items-center">
                                          <span class="trendIcon mr-2" :class="{'trendUp': oknha.bs == 'buy', 'trendDown': oknha.bs == 'sell'}"></span> 
                                          <span class="uppercase">{{ oknha.bs }}</span>
                                      </span> 
                                      <span>${{ formatPrice(oknha.ab, 2) }}</span>
                                  </div>
                                  <div class="flex justify-between">
                                      <span class="text-xs time">{{ formatDay(oknha.d) + ' '  +formatHours(oknha.d) }}</span> 
                                      <span class="totalWin font-bold" :class="{'winUp': oknha.aw > 0, 'winDown': oknha.aw == 0}" >
                                          <span class="uppercase"></span> 
                                          <span>{{ oknha.aw > 0 ? '+' : '' }}</span>${{ oknha.aw > 0 ? formatPrice(oknha.aw, 2) : 0 }} 
                                      </span>
                                  </div>
                              </div>
                        </div>
                      </div>  
                </div>
              </vs-tab>
          </vs-tabs>
      </div>
    </VuePerfectScrollbar>
  </vs-sidebar>
</template>

<script>
import VuePerfectScrollbar from 'vue-perfect-scrollbar'
import AuthenticationService from '@/services/AuthenticationService'
import { isTablet, isMobile } from 'mobile-device-detect'
import moment from 'moment'

export default {
  props: {
    isSidebarActive: {
      type: Boolean,
      required: true
    },
    data: {
      type: Object,
      default: () => {},
    }
  },
  watch: {
    // isSidebarActive(val) {
    //   if(!val) return
    //   if(Object.entries(this.data).length === 0) {
    //     //this.initValues()
    //     this.$validator.reset()
    //   }else {
    //     let { token } = JSON.parse(JSON.stringify(this.data))
    //     this.token = token
    //     //this.initValues()
    //   }
    //   // Object.entries(this.data).length === 0 ? this.initValues() : { this.dataId, this.dataName, this.dataCategory, this.dataOrder_status, this.dataPrice } = JSON.parse(JSON.stringify(this.data))
    // }
  },
  data() {
    return {

      isCloseTab: true,
      isOpenTab: false,

      isDate: '',

      lastDate: '',
      listItem: [],
      listItemOpen: [],
      colorxChangePass:"#def1d1",
      popupActiveChangePass: false,
      // Data Sidebar
      SidebarHSSetting: false,
      sidebarDataSetting: {},
      settings: { // perfectscrollbar settings
          maxScrollbarLength: 60,
          wheelSpeed: .60,
      },
      isSideBar: isMobile || isTablet,
    }
  },
//   filters: {
//     currencydecimal (val) {
//       if(val == 'BTC') return val.toFixed(6)
//       if(val == 'ETH') return val.toFixed(4)
//       return val.toFixed(2)
//     }
//   },
  computed: {
    isSidebarActiveLocal: {
      get() {
        if(!!this.isCloseTab){
          this.getListBET();
        }
        if(!!this.isOpenTab){
          this.getListOrder();
        }
        return this.isSidebarActive
      },
      set(val) {
        if(!val) {
          this.$emit('closeSidebar')
          // this.$validator.reset()
          // this.initValues()
        }
      }
    },
  },
  methods: {

    fromatTimzoneD(value){
      if (value) {
          return moment(new Date(value)).format('DD/MM/YYYY')
      }
    },

    fromatTimzoneH(value){
      if (value) {
          return moment(new Date(value)).format('HH:mm:ss')
      }
    },

    formatPrice(value, minimum) {
        var formatter = new Intl.NumberFormat('en-US', {
            //style: 'currency',
            //currency: '',
            minimumFractionDigits: minimum
        });
        return formatter.format(value);
    },

    formatDay(value){
      if (value) {
          return moment(String(value)).format('DD/MM/YYYY')
      }
    },

    formatHours(value){
      if (value) {
          return moment(String(value)).format('HH:mm:ss')
      }
    },

    getListBET(){
      let list = JSON.parse(localStorage.getItem('stateOpen'))
      if(list){
        this.listItemOpen = list.l.bet[0].items.reverse();
      }else{
        this.listItemOpen = [];
      }
    },

    getListOrder(){
      AuthenticationService.getListHitoryOrder()
      .then((res)=>{
          if(res.data.success){
              this.listItem = res.data.data
          }
      })
    },

    showHoSoSetting(data){
      //let id = data
      this.toggleDataSidebar(true)
    },

    toggleDataSidebar(val=false) {
      this.SidebarHSSetting = val
    },
  },
  components: {
    VuePerfectScrollbar
  },

  mounted() {
    let stateOpen = localStorage.getItem('stateOpen');
    if (stateOpen) {
      const list = JSON.parse(stateOpen)
      if(list){
        this.listItemOpen = list.l.bet[0].items.reverse();
      } else {
        this.listItemOpen = [];
        this.getListOrder();
      }
    }

    window.addEventListener('stateOpen-localstorage-changed', (event) => {
      if (!event.detail.storage) {
        this.listItemOpen = [];
        this.getListOrder();
        return;
      }
      const list = JSON.parse(event.detail.storage)
      if(list){
        this.listItemOpen = list.l.bet[0].items.reverse();
      } else {
        this.listItemOpen = [];
        this.getListOrder();
      }
    });
  },

}
</script>

